import React from 'react';
import './App.css';

class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      folderList: [],
      activeFolder:{
        id:null,
        name:"",
      },
      activeItem:{
        id:null,
        title:"",
        completed: false,
      },
      editing:false,
      viewingItems:false,
    }
    this.fetchFolders = this.fetchFolders.bind(this);
    this.fetchTasks = this.fetchTasks.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getCookie = this.getCookie.bind(this);
    this.viewItems = this.viewItems.bind(this);

  };

  getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
  };


  componentWillMount(){
    if (this.state.viewingItems === false){
      this.fetchFolders()
    } else {
      this.fetchTasks()
    }

  };

  fetchTasks(){
    console.log("Fetching Tasks");
    fetch(`https://cristianestevez.pythonanywhere.com/api/folder/${this.state.activeFolder.id}/task-list/`)
    .then(response => response.json())
    .then(data =>
      this.setState({
        taskList:data
      })
      )
  };

  fetchFolders(){
    console.log("Fetching folders");
    fetch('https://cristianestevez.pythonanywhere.com/api/folder-list/')
    .then(response => response.json())
    .then(data =>
      this.setState({
        folderList: data,
      })
      )

  };

  handleChange(e){
    var name = e.target.name
    var value = e.target.value
    console.log("Name:" , name, "Value: ", value)
    this.setState({
      activeFolder:{
        ...this.state.activeFolder,
        name : value
      }
    })
  }

  handleSubmit(e){
    e.preventDefault();
    console.log("Folder:", this.state.activeFolder);

    var csrftoken = this.getCookie('csrftoken');
    var url = 'https://cristianestevez.pythonanywhere.com/api/folder-create/';

    fetch(url,{
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
        'X-CSRFToken': csrftoken,
      },
      body: JSON.stringify(this.state.activeFolder)
    }).then((response) => {
      this.fetchFolders()
      this.setState({
        activeFolder: {
          id: null,
          name: '',
        }
      })
    }).catch(function(error){
      console.log("ERROR:", error)
    })

  }

  // startEdit(folder){
  //   this.setState({
  //     activeFolder: folder,
  //     editing: true,
  //   })
  // }

  removeFolder(folder){
    var csrftoken = this.getCookie('csrftoken');

    var url = `https://cristianestevez.pythonanywhere.com/api/folder-delete/${this.state.activeFolder.id}`


  }

  viewItems(e){
    console.log(folder)
    this.setState({
      viewingItems:true,
      activeFolder:1
      })

  }

  render(){
    var folders = this.state.folderList
    var self = this


    if (this.state.viewingItems === false){
      var appTitle = 'Folders'
      return(
          <div id="container" className="container">
              <br></br>
              <p id="app-title" styles="font-weight: bold;">{appTitle}</p>

              <div id="todo-list">

                {folders.map(function(folder, index){
                  return(
                    <div key={index} className="todo-list flex-wrapper">
                      <div style={{flex:7}}>
                          <span className="title" name="folder-name">  -  {folders[index].name}</span>
                      </div>
                      <div style={{flex:2}}>
                          <a href="#" onClick={() => self.viewItems(folder)} className="view-items">View items</a>
                      </div>
                      <div style={{flex:1}}>
                          <a href="#" className="remove">Remove</a>
                      </div>
                    </div>
                  )
                })}

              </div>
              <br></br>
              <form id="form" onSubmit={this.handleSubmit}>
                <div className="form-row " styles="justify-content: space-between;">
                    <div className="form-group form-md-9" styles="width: 85%;" id="form-inputbox">
                        <input onChange={this.handleChange} id="folder-name" className="form-control" type="text" name="title" placeholder="New Folder" value={this.state.activeFolder.name}></input>
                    </div>
                    <div className="form-group form-md-3">
                        <input id="add-folder" className="btn btn-primary border-secondary form-control" type="submit" value="Add"></input>
                    </div>
                </div>
              </form>
          </div>
      )
    } 
    else {
      return (
        <p>This</p>
      )
    }
  }
};

export default App;


